// import fetch from "node-fetch";

// Information to reach API
import resolve from "resolve";

const API_URL = "https://api.openai.com/v1/engines/text-davinci-003/completions";
const api_key = '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';

//Asynchronous functions
const generateText = async(prompt) => {

    const data = JSON.stringify({
      prompt: prompt,
      temperature: 0.5,
      max_tokens: 500,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0
    });

    try {
        const response = await fetch(API_URL, {
            method: "POST",
            body: data,
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${api_key}`
            }
            });
            if(response.ok){
            const jsonResponse = await response.json();
            const responseArray = jsonResponse.choices[0].text.split(/\d+\.\s/g);
            const filteredResponse = responseArray.map((element) => {
                return element.replace(/\n|\d+\./g, "").trim();
            }).filter((element) => element !== "");
            return(filteredResponse);
        }
    } catch (error) {
            console.log(error);
        }
    }



// Usage example:
generateText("Give me 20 song recommendations for this prompt: driving down the 101 with the top down. Format the response with this convention Song Name - Artist Name 2. Song Name - Artist Name`").then((response) => {
  console.log(response);
}).catch((error) => {
  console.error(error);
});

export default {generateText};
