import React from 'react';
import './Track.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause, faWaveformLines} from '@fortawesome/free-solid-svg-icons';
<FontAwesomeIcon icon="fa-solid fa-waveform-lines" />
import playlist from "../Playlist/Playlist.js";


class Track extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playing: false,
      audio: new Audio(this.props.track.preview_url)
    };
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
    this.playPreview = this.playPreview.bind(this);
    this.pausePreview = this.pausePreview.bind(this);
  }

  addTrack(event) {
    this.props.onAdd(this.props.track);
    this.state.audio.pause();
    this.setState({playing: false});
  }

  removeTrack(event) {
    this.props.onRemove(this.props.track);
    this.state.audio.pause();
    this.setState({playing: false});
  }

  playPreview(event) {
    this.state.audio.play();
    this.setState({ playing: true });
  }

  pausePreview(event) {
    this.state.audio.pause();
    this.setState({ playing: false });
  }

  renderAction() {
    if (this.props.isRemoval) {
      return <button className="Track-action" onClick={this.removeTrack}>-</button>
    }
    return <button className="Track-action" onClick={this.addTrack}>+</button>;
  }

  render() {
    return (
        <div className="Track" onClick={this.state.playing ? this.pausePreview : this.playPreview}>
          <div className='ImagePlay' >
          <button className="playButton">
            {this.state.playing ? <FontAwesomeIcon icon={faPause} style={{width: '1rem'}} /> : null}
          </button>
          <div className="Track-image">
            <img src={this.props.track.image} alt="Album Art" style={{width: '2.5rem'}}/>
          </div>
          </div>
          <div className="Track-information">
            <h3>{this.props.track.name}</h3>
            <p>{this.props.track.artist} | {this.props.track.album}</p>
          </div>
          {this.renderAction()}
        </div>
    );

  }
}

export default Track;
