import React from 'react';
// import './App.css';

import Playlist from '../Playlist/Playlist.js';
import SearchBar from '../SearchBar/SearchBar.js';
import SearchResults from '../SearchResults/SearchResults.js';
import Spotify from '../../util/Spotify.js';
import OpenAiAPIRequest from "../../util/OpenAiAPIRequest.js";


class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      searchResults: [],
      playlistName: 'New Playlist',
      playlistTracks: [],

    };

    this.search = this.search.bind(this);
    this.openAiSearch = this.openAiSearch.bind(this);
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
    this.updatePlaylistName = this.updatePlaylistName.bind(this);
    this.savePlaylist = this.savePlaylist.bind(this);

  }

  search(term) {
    Spotify.search(term).then(searchResults => {
      this.setState({searchResults: searchResults});
    });
  }

  openAiSearch(term){
      const songList = OpenAiAPIRequest.generateText(`Give me a song recommendations for this prompt: ${term}`);
      console.log(songList);
      Spotify.openAiSearch(songList).then(searchResults => {
        this.setState({searchResults: searchResults})
      });
      //     this.setState({searchResults: searchResults.push(song)});
      // for (const song of songList){
      //   Spotify.openAiSearch(song).then(searchResults => {
      //     this.setState({searchResults: searchResults.push(song)});
      //   });
    // }
  }


  addTrack(track) {
    let tracks = this.state.playlistTracks;
    if (tracks.find(savedTrack => savedTrack.id === track.id)) {
      return;
    }

    tracks.push(track);
    this.setState({playlistTracks: tracks});
  }

  removeTrack(track) {
    let tracks = this.state.playlistTracks;
    tracks = tracks.filter(currentTrack => currentTrack.id !== track.id);

    this.setState({playlistTracks: tracks});
  }

  updatePlaylistName(name) {
    this.setState({playlistName: name});
  }

  savePlaylist() {
    const trackUris = this.state.playlistTracks.map(track => track.uri);
    Spotify.savePlaylist(this.state.playlistName, trackUris).then(() => {
      this.setState({
        playlistName: '[NAME PLAYLIST]',
        playlistTracks: []
      });
    });
  }



  // render() {
  //   return (
  //       <div>
  //         <h1>SOUND <span className="highlight">TRACK</span></h1>
  //         <div className="App">
  //           <SearchBar onSearch={this.openAiSearch}/>
  //           <div className="App-playlist">
  //             <SearchResults searchResults={this.state.searchResults}
  //                            onAdd={this.addTrack} />
  //             <Playlist playlistName={this.state.playlistName}
  //                       playlistTracks={this.state.playlistTracks}
  //                       onNameChange={this.updatePlaylistName}
  //                       onRemove={this.removeTrack}
  //                       onSave={this.savePlaylist} />
  //           </div>
  //         </div>
  //       </div>
  //   );
  // }

}

// Usage example:


export default App;
