import React from 'react';
import './Track.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause } from '@fortawesome/free-solid-svg-icons';

class Track extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      playing: false
    };
    this.audio = new Audio(this.props.track.preview_url);
    this.playPreview = this.playPreview.bind(this);
    this.pausePreview = this.pausePreview.bind(this);
  }

  componentWillUnmount() {
    this.audio.pause();
  }

  playPreview() {
    this.props.onPlay(this.props.track.id);
    this.audio.play();
    this.setState({ playing: true });
  }

  pausePreview() {
    this.props.onPause(this.props.track.id);
    this.audio.pause();
    this.setState({ playing: false });
  }

  renderAction() {
    if (this.props.isRemoval) {
      return (
          <button
              className="Track-action"
              onClick={(e) => {
                this.props.onRemove(this.props.track);
                e.stopPropagation();
              }}
          >
            -
          </button>
      );
    }
    return (
        <button
            className="Track-action"
            onClick={(e) => {
              this.props.onAdd(this.props.track);
              e.stopPropagation();
            }}
        >
          +
        </button>
    );
  }

  render() {
    const isPlaying = this.props.currentTrackId === this.props.track.id && this.state.playing;
    return (
        <div className="Track" onClick={isPlaying ? this.pausePreview : this.playPreview}>
          <button className="playButton">
            {isPlaying ? (
                <FontAwesomeIcon icon={faPause} style={{ width: '1rem' }} />
            ) : (
                <FontAwesomeIcon icon={faPlay} style={{ width: '1rem' }} />
            )}
          </button>
          <div className="Track-image">
            <img src={this.props.track.image} alt="Album Art" style={{ width: '2.5rem' }} />
          </div>
          <div className="Track-information">
            <h3>{this.props.track.name}</h3>
            <p>
              {this.props.track.artist} | {this.props.track.album}
            </p>
          </div>
          {this.renderAction()}
        </div>
    );
  }
}

export default Track;
